import React, { useEffect } from "react";

function Link({ mess, open }) {
  //   useEffect(() => {
  //     console.log(mess);
  //   }, [open]);
  useEffect(() => {
    positionEv();
  }, []);
  const positionEv = (e) => {
    console.log("render");
    if (!mess) return setTimeout(positionEv, 0);
    let posi = dom.getBoundingClientRect();
    console.log(posi);
    console.log(mess.top - posi.top);
    dom.style.top = -(mess.top - posi.top) + "px";
    console.log(dom);
  };
  let dom;
  return (
    <div className={`site ani pointer test`} ref={(ref) => (dom = ref)}>
      <div className="backcolor">
        <h3>UI 参考网站 -- dribbbie</h3>
      </div>
      <style jsx>{`
        .site {
          width: 80%;
          margin: 20px 0 20px 20%;
          background: #212329;
          padding: 10px;
          border-radius: 4px;
          position: relative;
          top: 0;
        }
        .close {
          position: absolute;
          top: 0;
          z-index: -2;
        }
        .site .backcolor {
          background: #4eac6d;
          border-radius: 4px;
          text-align: center;
          padding: 10px;
          font-size: 20px;
        }
      `}</style>
    </div>
  );
}

export default Link;
