import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
const Footer = ({ color, loading }) => {
  console.log(color);
  console.log(loading);
  const [show, setShow] = useState(false);
  useEffect(() => {
    const Event = () => {
      let winH = window.innerHeight;
      let docH = document.documentElement.offsetHeight;
      let scrH = document.documentElement.scrollTop;
      docH - scrH - 30 <= winH ? setShow(true) : setShow(false);
    };
    window.addEventListener("scroll", Event);
  }, []);
  return (
    <footer className={`row mx-0 p-0 ${show ? "show" : ""}`}>
      <div className="col-12 py-5 m-0 text-center">
        <h3>2020 Have a good night</h3>
      </div>
      <style jsx>
        {`
          footer {
            background: ${color};
            color: white;
            right: 0;
            left: 0;
            bottom: 0;
            position: fixed;
            opacity: 0;
            transition: 0.5s;
            transform: translateZ(0);
          }
          .show {
            opacity: 1;
          }
          h3 {
            font-size: 2rem;
          }
        `}
      </style>
    </footer>
  );
};
const stateEvent = (state) => ({
  loading: state.TypeReducer.loading,
});

export default connect(stateEvent)(Footer);
