import React, { useEffect, useState } from "react";

function Photo({ id }) {
  const [blob, setBlob] = useState(null);
  const [uncount, setUncount] = useState(false);

  useEffect(() => {
    getPhoto();
    return () => {
      setUncount(true);
      if (blob) {
        URL.revokeObjectURL(blob);
      }
    };
  }, [id]);
  const getPhoto = async () => {
    try {
      const QURL = process.env.DATA_URL + id;
      let res = await fetch(QURL);
      if (res.ok) {
        let result = await res.blob();
        if (!uncount) {
          setBlob(result);
        }
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      {blob && <img src={URL.createObjectURL(blob)} alt="照片" />}
      <style jsx>{`
        img {
          width: 100%;
        }
      `}</style>
    </>
  );
}

export default Photo;
