import axios from "axios";

const feature = (url, method, type = "json", data) => {
  if (!url) throw new Error("缺少url属性");
  if (!method) throw new Error("缺少方法属性");
  return new Promise((resolve, reject) => {
    switch (method) {
      case "get":
        axios.get(url, {
          responseType: type
        }).then(res => resolve(res.data)).catch(err => reject(err));
        break;

      case "post":
      case "put":
      case "delete":
        if (!data) {
          reject(new Error("没有data数据"));
          break;
        }

        axios({
          url,
          method,
          data
        }).then(res => resolve(res.data)).catch(err => reject(err));
        break;

      default:
        reject(new Error("没有匹配的方法"));
    }
  });
};

export default feature; // 获取数据 func(url,'get')
