import React, { useEffect, useState } from "react";
import axios from "axios";
const Photo = () => {
  const [buf, setbuf] = useState(null);
  const [back, setback] = useState(false);
  const [err, seterr] = useState(false);
  useEffect(() => {
    const getBuf = async () => {
      try {
        let res = await axios("https://picsum.photos/600/300", {
          responseType: "blob",
        });
        console.log(res.data);
        setbuf(res.data);
      } catch (err) {
        console.log(err);
        seterr(true);
      }
    };
    getBuf();
    return () => {
      setback(true);
      if (buf) URL.revokeObjectURL(buf);
    };
  }, []);
  return (
    <div>
      {buf ? (
        <img src={URL.createObjectURL(buf)} alt="照片" />
      ) : (
        <div className="anima"></div>
      )}

      <style jsx>{`
        .anima {
          width: 100%;
          height: 200px;
          animation: show 2s infinite;
        }
        img {
          width: 100%;
        }
        @keyframes show {
          0% {
            background: #202930;
          }
          50% {
            background: #0f171d;
          }
          100% {
            background: #202930;
          }
        }
      `}</style>
    </div>
  );
};

export default Photo;
