import React ,{useState} from 'react'

const Photo = () => {
    const [photo, setPhoto] = useState(null)
    
    const fileChange = (e) => { 
         let file = e.target.files[0];
         try {
              let blob = URL.createObjectURL(file);         
              setPhoto(blob);
         }catch(err) {
              console.log(err)
         }                  
    } 
   const clickFile = ( e ) => {           
           e.target.nextElementSibling.click();
   }
   const dbclick = (e) => {
       setPhoto('')
       e.target.previousElementSibling.value='';
   }

    return (
        <div className="col-10 mt-5">
             {  
                 !photo &&   <div className="load-photo row" 
                                  onClick={clickFile}>                                
                             </div>                                                      
             }
             <input type="file" name="photoFile"
                                className='d-none'
                                onChange={fileChange}
                                />
             { photo &&   <img src={photo} alt="code-photo" onDoubleClick={dbclick}/> }
             <style jsx> {`
                        .load-photo {
                            height:400px;
                            background:#4b30c3;
                        }
                        img {
                            width:100%;
                        }
                        `}
             </style>
             
        </div>
    )
}

export default Photo
