import React, { useEffect, useState } from "react";
import axios from "axios";
const Photo = () => {
  const [buf, setbuf] = useState(null);
  const [back, setback] = useState(false);
  const [err, seterr] = useState(false);
  const [load, setload] = useState(true);
  useEffect(() => {
    getBuf();
    return () => {
      setback(true);
      if (buf) URL.revokeObjectURL(buf);
    };
  }, []);
  const getBuf = async () => {
    try {
      let res = await axios("https://picsum.photos/400/300", {
        responseType: "blob",
      });
      setload(false);
      !back ? setbuf(res.data) : null;
    } catch (err) {
      console.log(err);
      seterr(true);
    }
  };
  const restartLoadPhoto = () => {
    seterr(false);
    getBuf();
  };
  return (
    <div>
      {load && <div className="anima"></div>}
      {buf && <img src={URL.createObjectURL(buf)} alt="照片" />}
      {err && <div className="error" onClick={restartLoadPhoto}></div>}
      <style jsx>{`
        .anima {
          width: 100%;
          height: 300px;
          animation: show 2s infinite;
        }
        .error {
          width: 100%;
          height: 300px;
          background: red;
        }
        img {
          width: 100%;
        }
        @keyframes show {
          0% {
            background: #202930;
          }
          50% {
            background: #0f171d;
          }
          100% {
            background: #202930;
          }
        }
      `}</style>
    </div>
  );
};

export default Photo;
