import React,{ useState , useEffect , useRef} from 'react'
import Photo from './photo'

  

const ArtContent = ({setLinkList}) => {
    useEffect(() => {                    //初次加载文档 检测是否有内容事件
        let Value =    localStorage.getItem('article-content-object') ;         
        if(!Value) {
            let data = JSON.stringify(
                [{
                key:'content-id-1',
                type:'text',
                value :''},]
            );
            localStorage.setItem('article-content-object',data) ;
            return;
        }
        let dataList = JSON.parse(Value)       
        setInitialTextareList(dataList);
    },[])
    const [initialTextareList, setInitialTextareList] = useState([{
        key:'content-id-1',
        type:'text',
        value :''},]);
    
    const inputEv = (e,index) => {        //设置文档内容事件        
        let dom = e.target ;             // 内容高度自动适应                    
        dom.style.height = dom.scrollHeight > dom.offsetHeight ? dom.scrollHeight+10+'px': dom.offsetHeight+'px';
            
        let oldValue =    localStorage.getItem('article-content-object') ;   
        if(!oldValue) return
        let mianData = JSON.parse(oldValue)                  // 根据输入的值 更新状态和localstorage！！
        mianData[index].value = e.target.value;                    
        localStorage.setItem('article-content-object',JSON.stringify(mianData)) ;
        setInitialTextareList(mianData);
    }
    
    const deleteEv = (index) => {   //删除掉文章 段落事件
          let content = initialTextareList.filter(item => item.type === 'text');          
          if(content.length === 1) return         
          let newList = initialTextareList.filter( (e,Cindex) => Cindex !== index)
          setInitialTextareList(newList);
          localStorage.setItem('article-content-object',JSON.stringify(newList)) ;
    }
    return (
        <div className='row Content  mx-0'>
            <AddG setList={setInitialTextareList} id={initialTextareList} setLinkList={setLinkList}/>
            <div className="col-8 offset-2">
                {
                    initialTextareList.map(
                        (e,index) => {
                            if(e.type === 'text') return (
                                <div className="row content-item position-relative" key={e.key} >
                                    <textarea name="message" required={index === 0 ? true : false} value={e.value} className='w-100 px-4'
                                        onInput={(e) => inputEv(e,index)}
                                        onChange={(e) => inputEv(e,index)}>
                                    </textarea>
                                    <div className="delete" onClick={() => deleteEv(index)}>
                                        <i className='iconfont icon-trash-'></i>
                                    </div>                     
                                </div> 
                            )
                            if(e.type === 'photo') return (                               
                                 <Photo key={e.key} state={initialTextareList} setState = {setInitialTextareList} title={e.title} index={index} name={e.key}/>  
                            )
                        }
                    )
                }                                                       
            </div>
            <style jsx> {`

               .Content {
                    margin-top:10rem;  
                    border-bottom:2px solid #eaeaea;
               }
               
               .content-item {
                   margin-bottom:5rem;
               }
            
              
               textarea {
                   font-size:2.5rem;
                   line-height:2;  
                   min-height:40vh;               
               }
               .delete {
                   position:absolute;
                   right:-50px;
                   top:50%;
                   
               } 
               .delete i {
                     background:none;
                     color:black;                     
               }
               .delete i:hover {
                    background:black;
                     color:white;                     
               }
               i {
                   color:white;
                   font-size:2rem;
                   padding:0.5rem;
                   background:#3a5999;
                   border-radius:50%;
                   cursor:pointer;
               }              
               
                                                                `}</style>
        </div>
    )
}
const AddG =React.memo(({setList,setLinkList}) => {    
    
    const addText = () => {
         let oldValue = localStorage.getItem('article-content-object');         
         if(!oldValue) return
         
         let value = JSON.parse(oldValue);            
         value.push(
                {
                    key:'content-id-'+(value.length+1),
                    type:'text',                    
                    value :''},
         )            
         
        let data = JSON.stringify(value);
        localStorage.setItem('article-content-object',data);  
        setList(value);
    }
    const addPhoto = () => {
        let oldValue = localStorage.getItem('article-content-object');         
        if(!oldValue) return        
        let value = JSON.parse(oldValue);            
        value.push(
               {
                   key:'content-id-'+(value.length+1),
                   type:'photo', 
                   title:''                   
                },
        )            
        
       let data = JSON.stringify(value);
       localStorage.setItem('article-content-object',data);  
       setList(value);
   }
   const addLink = () => {
       setLinkList( preState => [...preState,'a'])
   }
    return (
        <div className='addG'>
               <ul>
                   <li>
                      <i className='iconfont icon-file-text' onClick={ addText }></i>
                   </li>
                   <li>
                      <i className='iconfont icon-image' onClick={ addPhoto }></i>
                   </li>                  
                   <li>
                      <i className='iconfont icon-link' onClick={addLink}></i>
                   </li>  
               </ul>
               <style jsx> {`
                        .addG {
                            position:fixed;
                            top:50%;
                            left:0;
                            transform:translateY(-50%);
                        }
                        li {
                            height:40px;
                            width:40px;
                            background:black;
                            border-radius:50%;
                            cursor:pointer;
                            display:flex;
                            justify-content:center;
                            align-items:center;
                            margin:1rem 0.5rem;
                            transition:.4s;
                        }
                        li:hover {
                            transform:scale(1.2);
                        }
                        i {
                            color:white;
                            font-size:20px;
                        }
                        `}
               </style>
        </div>
    )
}) 



export default ArtContent
