import React, { useState, useEffect } from "react";

const Index = () => {
  const [oepn, setoepn] = useState(false);
  let arr = [1, 3, 4, 2];
  const oepnEvent = () => {
    setoepn((pre) => !pre);
  };
  return (
    <div className="box">
      <button onClick={oepnEvent}>open</button>
      {open && (
        <div className="ctn">
          {arr.map((a, i) => (
            <div className="box" key={i}></div>
          ))}
        </div>
      )}
      <style jsx>{`
        .ctn {
          background: blue;
          width: 100%;
        }
        .box {
          width: 80%;
          height: 60px;
        }
      `}</style>
    </div>
  );
};

export default Index;
