import React ,{useState} from 'react'
import Loading from '../public/loading'
import Router from 'next/router'

const PostBu = () => {
    const Open = async (e) => {
          if(!status) {
               setstatus(true);
                 e.target.style.background = '#e65e2c';
          }else{
            let data = new FormData();
            data.append('key',e.target.nextElementSibling.value);  
            try {
                setFetching(true)
                const QURL = process.env.DATA_URL + 'login/key'
                let res = await fetch(QURL,{
                    method:'POST',
                    body:data,
                })         
                let result = await  res.json();    
                if(res.ok) {                                                
                    Router.push({
                        pathname:'/release-article',
                        query: { key : result },
                    },'/release-article')
                }else{  
                    setFetching(false)                   
                    alert(result);
                }
             }catch(err) {
                setFetching(false)   
                console.log(err);
             }   
          }
    }
    const [status, setstatus] = useState(false);
    const [fetching, setFetching] = useState(false);
    return (
         <div className="row send-article">
                    <div className="nav-title col-12">
                        <h1>
                            POST ARTICLE
                        </h1>
                    </div>
                    <div className="article-button col-12 my-5">
                        <button className= 'shadow' onClick={Open} >
                            {!fetching ? 'NEW ARTICLE': <Loading color='white' size='20'/>}
                        </button>
                      {status &&  <input type="password" autoFocus name="key" id="key" placeholder='key' />}
                    </div>
                    <style jsx> {`
                    .send-article {
                            position:absolute;
                            left:10%;
                            top:25rem;
                            color:white;
                        }
                         
                    h1 {
                        font-size:6rem;
                    }
                    .article-button input {
                        margin-left:4rem;
                        height:100%;
                        width:50%;
                        border:none;
                        border-bottom:2px white solid;
                        font-size:4rem;
                        font-weight:700;
                        color:#e65e2c;
                        background:none;
                        outline:none;
                        animation:show 1s 1;
                    }
                    .article-button input:focus {
                        border-color:#e65e2c;
                    }
                    button {
                        border:none;
                        background:#326fa0;
                        border-radius:6px;
                        padding:2rem 4rem;
                        font-weight:600;
                        color:white;
                        font-size:2rem;
                        transition:.4s;
                    }
                   
                    button:hover {
                        transform:translateX(10%);
                    }
                    @keyframes show {
                        0% {
                            width:0;
                        }
                        100% {
                            width:50%;
                        }
                    }
                    @media (max-width:1200px) {
                        .send-article {                        
                            top:17rem;                          
                        }
                        h1 {
                            font-size:3rem;
                        }
                        .article-button input {
                            font-size:1.5rem;
                        }
                        button {
                            padding:0.8rem 1.4rem; 
                        }
                    } 
                    @media (max-width:600px) {
                        .send-article {                        
                            top:15rem;                          
                        }
                        h1 {
                             font-size:2rem;
                        }
                        button {
                            font-size:1.6rem;
                        }
                    }
                    @media (max-width:440px) {
                        .send-article {                        
                            top:10rem;                          
                        }
                        h1 {
                             font-size:2rem;
                        }
                        button {
                            font-size:1.6rem;
                        }
                    }
                        `}
                     </style>
      </div>            
    )
}

export default PostBu
