import React from 'react'

const MessageWindow = ({set}) => {
    const closeEvent = (e) => {
         let dom = e.target.closest('.window-root');
         if(!dom) set(false);
    }
    return (
        <div className='window-message' onClick={closeEvent}>
            <div className="window-root">
                <textarea type="text" name="message-main"  autoFocus id="message-main"/>                        
                <button>Send</button>
            </div>
            <style jsx> {`
                  .window-message{
                      position:fixed;
                      left:0;
                      right:0;
                      top:0;
                      bottom:0;                                            
                      background:rgba(0,0,0,0.6);                      
                      display:flex;
                      justify-content:center;
                      align-items:center;
                      z-index:5;
                  }          
                  .window-root {
                      width:600px;
                      height:360px;
                      background:#17334b;    
                      display:flex;   
                      justify-content:space-around; 
                      align-items:center;
                      flex-direction:column;                                   
                      border-radius:25px;
                      overflow:hidden;                      
                  }
                  textarea {
                      height:60%;
                      padding:2rem;
                      width:80%;
                      border-radius:12px;  
                      font-size:2rem;
                      font-weight:700;                    
                  }
                  button {
                      background:white;
                      color:#2b5875;                      
                      padding:0.5rem 2rem;
                      font-weight:700;
                      font-size:2rem;
                      width:35%;
                      border:none;
                      border-radius:12px;
                      transition:.2s;
                  }
                  button:hover {
                    width:38%;
                  }
                            `}
            </style>
        </div>
    )
}

export default MessageWindow
