import React, { useEffect, useState, useRef } from "react";
import Photo from "./photo";

const PostPhoto = ({ data }) => {
  const scrollEv = () => {
    let dom = photosDom.current;
    if (dom) {
      let top = dom.getBoundingClientRect().top;
      if (top < 300) {
        dom.querySelector(".left-photos").classList.add("left-photos-show");
      }
      if (top > window.innerHeight - 200) {
        dom.querySelector(".left-photos").classList.remove("left-photos-show");
      }
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", scrollEv);
  }, []);
  let photosDom = React.createRef();
  return (
    <div className="row justify-content-center mx-0" ref={photosDom}>
      <div className="col-10 col-xl-8 ">
        <h2 className="text-lg-left text-center">一些 图片 </h2>
        <div className="row overflow-hidden">
          <div className="col-12 col-md-6 mx-0 left-photos">
            <div className="row justify-content-between ">
              {data &&
                data.list.map((photo) => (
                  <div className="col-6 " key={photo}>
                    <Photo name="图片" id={photo} />
                  </div>
                ))}
            </div>
          </div>
          <div className="col-12 col-md-6 max-photo mx-0">
            <Photo name="图片" id={data.first} classname="right" />
          </div>
        </div>
      </div>
      <style jsx>
        {`
          h2 {
            font-size: 4rem;
            margin: 5% 0;
            font-weight: 700;
          }
          .left-photos {
            transform: translateX(100%);
            transition: 1s;
          }
          .left-photos-show {
            transform: translateX(0);
          }
          .justify-content-between {
            margin-bottom: 30px;
          }
          .justify-content-between .col-6:nth-child(n + 3) {
            margin-top: 30px;
          }

          .max-photo img {
            height: calc(100% - 30px);
          }
        `}
      </style>
    </div>
  );
};

export default PostPhoto;
