import React from 'react'
import Photo from './photo'

const Item = React.memo( ({message,setCode,type}) => {   
        
        if(!message.filter){
            return   (
                 <div className="col-12 col-md-6">  
                       <div className="row justify-content-center align-items-center">
                           { type === 'css'? <Photo id={message.Componentphoto}/> :<img src={`kuangjia/${type}.png`} alt="react" className='code-photo'/>                                                          }
                       </div>
                       <h2 className='imte-title text-center'>
                           {message.ComponentTitle}
                       </h2>                             
                       <div className="row justify-content-center my-5" >
                           <button onClick={() => setCode(message.ComponentCode)}>
                               Cody
                           </button>
                       </div>  
                       <style jsx> {`
                       img {
                        width:100%;
                      }                       
                        h2 {
                            font-size:4rem;
                            font-weight:700;
                            margin:2rem 0 ;                       
                        } 
                        .code-photo {
                            width:100px;
                            height:100px;
                        }   
                        button {                       
                            border: none;
                            background: #fdd303;
                            color: white;
                            border-radius: 6px;
                            font-size: 2rem;
                            padding:1rem 3rem;
                            font-weight: 700;
                            -webkit-transition: .2s;
                            transition: .2s;                       
                        }   
                        button:hover {
                            padding:1rem 4rem;
                        } 
                                                `}
                            </style>
              
                 </div> )
           }else{
               return null
           }
   
})

export default Item
