import React ,{useState ,useEffect} from 'react'

const Sort = ({selectList}) => {    
    const [sortValue, setSortValue] = useState('')
    const [list, setList] = useState([])
    useEffect(() => {
          setList(selectList)    

    },[selectList])
    const changeValue = () => {
               if(sortValue === '') return
               setList( preState => [...preState,sortValue])               
               setSortValue('')
               let dom = selectDom;
               setTimeout( () => {                   
                   dom.value = sortValue
               },[100])
    }
    let selectDom
    return (
        <div className='row sort ml-4'>
            <select  name="simple  col-12" className='component-sort-name' ref={ref => selectDom = ref }>                
                {
                  list &&  list.map(
                        (e,index) => (
                            <option value={e} key={index}>
                                {e}
                            </option>
                        )
                    )
                }
            </select>            
            <div className="col-12 sortInput px-0">
               <input type="text" name="sortItem" id="sortItem" 
                     value={sortValue}
                     onChange={e => setSortValue(e.target.value)}         
                     onInput={e => setSortValue(e.target.value)}         
                     className=' mt-3'/>
            </div>
            <div className="col-12 px-0 mt-3">
                <button onClick={changeValue}>
                    Add
                </button>
            </div>

            <style jsx> {`
                   select {
                       border:none;
                       width:100%;
                       text-align:center;
                       padding:1rem;
                       font-size:2rem;
                       font-weight:600;
                   }  
                   option {
                       padding:0.5rem;
                   }   
                   input {
                       width:100%;
                       border:none;
                       padding:0.5rem;
                       font-size:2rem;
                   }
                   button {
                       width:100%;
                       border:none;
                       background:#4b30c3;
                       color:white;
                       padding:0.5rem;
                       font-size:2rem;
                   }
                   
                        `}
            </style>
        </div>
    )
}

export default Sort
