import React, { useEffect, useState } from "react";
import Head from "next/head";
import fetch from "node-fetch";
import Router from "next/router";

import Nav from "../component/public/nav";
import Author from "../component/article/author";
import ArtContent from "../component/article/articleContent";
import About from "../component/article/about";
import Comment from "../component/article/comment";
import Footer from "../component/home/footer";

const Home = ({ data }) => {
  const fontsizeEvent = () => {
    let style = document.documentElement.style;
    if (window.innerWidth < 960) {
      style.fontSize = "6px";
    } else {
      style.fontSize = "10px";
    }
  };
  useEffect(() => {
    if (!data) {
      Router.replace("/404");
    }
    fontsizeEvent();
    window.addEventListener("resize", () => {
      fontsizeEvent();
    });
  }, []);
  const [state, setState] = useState(data);

  const [routerStatus, setRouterStatus] = useState(false); // 路由变化事件
  const handleRouteChange = (url) => {
    setRouterStatus(true);
  };
  Router.events.on("routeChangeStart", handleRouteChange);
  return (
    <>
      <Head>
        <link
          rel="stylesheet"
          href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
          integrity="sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh"
          crossorigin="anonymous"
        ></link>
        <link
          rel="stylesheet"
          href="//at.alicdn.com/t/font_1748919_piihixc593j.css"
        />
        <link rel="icon" href="/logo.jpg" type="image/x-icon"></link>
        <title> 文章 </title>
      </Head>
      <div className="container-fluid">
        <nav>
          <Nav>
            <img src="article-bg.jpg" alt="bg" />
          </Nav>
        </nav>
        {state && (
          <>
            <Author {...data} />

            <ArtContent content={data.ArticleContent} />

            <About
              linkList={data.ArticleAboutLink}
              message={data.ArticleAboutIntroduction}
            />

            <Comment message={data.comment} id={data._id} />

            <Footer />
          </>
        )}
        {routerStatus && (
          <div className="loadAnima"> {/*  路由变化时 提示动画 */}</div>
        )}
      </div>
      <style jsx>
        {`
          .loadAnima {
            position: fixed;
            left: 0;
            top: 0;
            right: 0;
            bottom: 0;
            background: black;
            animation: Loading 2s 10;
            z-index: 9999;
          }
          @keyframes Loading {
            0% {
              opacity: 0;
            }
            50% {
              opacity: 1;
            }
            100% {
              opacity: 0;
            }
          }
        `}
      </style>
      <style jsx global>{`
        *,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
        html {
          font-size: 10px;
        }
        .container-fluid {
          padding: 0;
        }
      `}</style>
    </>
  );
};
export const getServerSideProps = async (context) => {
  const RURL =
    process.env.DATA_URL + "addArticle/gotArticle/" + context.query.id;
  let res = await fetch(RURL);
  if (res.status === 404) return { props: { data: null } };
  let result = await res.json();
  return {
    props: {
      data: result,
    },
  };
};
export default Home;
