import React,{useEffect} from 'react'
import {setLocalstorage,setLocalValue} from './localstorage'

const About = ({List}) => {
   useEffect(() => {       
       setLocalValue("textarea[name='about-article-text']",'about-article-text')
   },[])
    return (
        <div className='row about m-0'>
            <div className="col-8  offset-2">
                <div className="row">
                    <h3 className='my-4 col-12'>
                        ABOUT ARTICLE
                    </h3>
                    <div className="col-12 content-item">
                        <textarea name="about-article-text" onChange={(e) => setLocalstorage('about-article-text',e.target.value) } className='w-100 h3 p-4' required>
                               
                        </textarea>                     
                    </div> 
                    <div className="col-12 content-item my-4">
                         {
                           List  && List.map(
                                 ( e , index ) => (
                                    <React.Fragment key={index}>
                                        <input type="text" name={`a-link-${index}`} placeholder='Link'  className='px-4 py-2 col-6 h4'
                                                onChange={ (e) => setLocalstorage('about-article-link',e.target.value) }
                                                required/>                    
                                        <input type="text" name={`a-link-title-${index}`} placeholder='Link Title' className='px-4 py-2 ml-5 h4'
                                                onChange={ (e) => setLocalstorage('about-article-linkTitle',e.target.value) }
                                        required/>
                                    </React.Fragment>    
                                 )
                             )
                         }                                              
                    </div>  
                    <div className="col-12">
                        <div className="row justify-content-end">
                            <button type="submit shadow-sm">
                                Commit
                            </button>
                        </div>
                    </div>                  
                </div>
            </div>
            <style>{`
                .about {
                    border-bottom:2px solid #eaeaea;
                    padding:10rem 0;
                }
                h3 {
                   font-size:2.5rem;
                   line-height:2;
               }
               h4 {
                   font-size:2rem;
                   line-height:1.5;
               }
               textarea  {
                   min-height:15vh;                  
               }
                button {
                    border:none;
                    background:black;
                    color:white;
                    padding:1rem 2rem;
                    font-size:2rem;
                    transition:.4s;
                    font-weight:700;
                }
                button:hover {
                    background:white;
                    color:black;
                }
            `}</style>
        </div>
    )
}

export default About
