import React, { useEffect, useState } from "react";
import Nav from "./nav";
import Sort from "./sort";
import Photo from "./photo";

const LeftRoot = () => {
  useEffect(() => {
    reqData();
  }, []);
  const reqData = async () => {
    const QURL = process.env.DATA_URL + "getComponentList/all";
    let res = await fetch(QURL);
    if (res.ok) {
      let data = await res.json();
      setTypeList(data);
      setSortList(data[0].typeList);
    }
  };
  const [sortList, setSortList] = useState([]);
  const [typeList, setTypeList] = useState([]);
  const changeList = (data) => {
    if (Array.isArray(data)) {
      setSortList(data);
    }
  };
  return (
    <div className="left-root row justify-content-center">
      <div className="col-6">
        {typeList.length > 0 && <Nav list={typeList} setList={changeList} />}
      </div>
      <div className="col-4">
        <Sort selectList={sortList} />
      </div>
      <Photo />
    </div>
  );
};

export default LeftRoot;
