import React,{useRef , useEffect} from 'react'

const Video = () => {
    const positionEvent = (count,dom) => {
        try {          
            dom.children[0].style.marginLeft = count+'px';
        }catch(err){
            console.log(err);
        }  
    }
    const leaveEv = (e) => {
          positionEvent(0,e.currentTarget)                                         
    }
    const enterEv = (e) => {    
        let dom = e.currentTarget;
        let count = dom.clientWidth - dom.children[0].offsetWidth;       
        positionEvent(count,dom)                   
    }
    const scrollEv = () => {                         //滚动动画事件
        let dom = videoRef.current;
        if(dom){                                                       
            if(dom.getBoundingClientRect().top <  (window.innerHeight - 600)) {                                                                                       
                let domList = dom.querySelectorAll('img');
                Array.from(domList).map(e => e.classList.add('show'))
            }            
         }          
    }
   
    useEffect(() => {
       window.addEventListener('scroll',scrollEv);                         
    }, [])
    let videoRef = React.createRef();
    return (
        <div className='row video justify-content-center mx-0' ref ={videoRef}>            
            <div className="col-12 col-sm-10 col-xl-8 my-4">
                <div className="row video-ctn">
                    <div className="col-12 col-xl-4 text-xl-left text-center">
                       <h2 >
                           Series of videos
                       </h2>
                       
                       <h3 className='my-4'>
                           Click on you are interested  !
                       </h3>
                    </div>
                    <div className="col-12 col-xl-8 mt-5 mt-xl-0">
                        <div className="row video-root justify-content-center my-xl-4 ">
                            <div className="col-5 video-item shadow-sm mx-4" onMouseLeave={leaveEv} onMouseEnter={enterEv}>                                  
                                    <img src="app-logo/ar.jpg" alt="ae" />                                                                                    
                            </div>
                            <div className="col-5 video-item shadow-sm mx-4 "onMouseLeave={leaveEv} onMouseEnter={enterEv}>
                                 <img src="app-logo/pr.jpg" alt="pr"/>
                            </div>
                        </div>
                        <div className="row video-root justify-content-center my-xl-4 ">
                            <div className="col-5 video-item shadow-sm mx-4 "onMouseLeave={leaveEv} onMouseEnter={enterEv}>
                                  <img src="app-logo/ps.jpg" alt="ps"/>
                            </div>
                            <div className="col-5 video-item shadow-sm mx-4 "onMouseLeave={leaveEv} onMouseEnter={enterEv}>
                                  <img src="app-logo/all.jpg" alt="all"/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <style jsx> {`
                    .video-ctn {
                      border-bottom:2px solid #eaeaea;
                      padding:20rem 0;  
                    }
                    img {
                        height:100%;                                                                       
                        transition:.5s;
                        margin-left:25px;
                    }    
                    img.show {
                       margin-left:0;
                    }
                    .photo {
                        width:16vh;
                        margin-left:0;
                        transition:1.5s;                        
                    }                                  
                    h2 {
                      font-size:4rem;
                      font-weight:700;      
                      animation: text 0.6s 1 0s ;                                   
                   }
                   h3 {             
                      text-decoration:underline;  
                      animation: text 0.6s 1 0.2s ;
                   }
                 .video-item {
                     height:16vh;   
                     padding:0;    
                     cursor:pointer;                                 
                     background:rgba(0,0,0,0.8);  
                     margin-bottom:4rem;                                                                                              
                 } 
                 
                 @keyframes text {
                            0% {
                                transform:translateY(100%);
                                opacity:0;
                            }
                            100% {
                                transform:translateY(0);
                                opacity:1;
                            }
                        }
              
                 .video {
                     background:#eff2f7;
                     background: linear-gradient(45deg,white,#fbfcfe);
                     background:radial-gradient(#f4f7fc,white);                                      
                     margin-top:4rem;
                 }
                 @media (max-width:960px) {
                    .video-root {
                         margin:4rem
                        }
                 }                 
                 
                 @media (max-width:480px) {
                    .video-item{
                        height:auto;
                    }
                    img {
                        width:100%;
                        height:auto;
                    }
                   
                 } 
            
                        `}
            </style>
        </div>
    )
}

export default Video
