import React, { useState, useEffect, useRef } from "react";
import Link from "./link";
export default () => {
  const [POSI, setPOSI] = useState(null);
  const [open, setopen] = useState(false);
  const [he, sethe] = useState(0);
  useEffect(() => {
    setTimeout(() => {
      let MainPosi = mainDom.getBoundingClientRect();
      sethe(heightDom.offsetHeight);
      setPOSI(MainPosi);
    });
  }, []);
  let mainDom;
  let heightDom;
  return (
    <div className="link-ctn" ref={(ref) => (mainDom = ref)}>
      <div className="main pointer ani">
        <div className="color"></div>
        <div className="title">
          <h2>CSS</h2>
        </div>
      </div>
      <div
        className="main pointer ani nice"
        ref={(ref) => (heightDom = ref)}
        onClick={() => {
          setopen((pre) => !pre);
        }}
      >
        <div className="color"></div>
        <div className="title">
          <h2>CSS</h2>
        </div>
      </div>
      <Link mess={POSI} open={open} />
      <Link mess={POSI} open={open} />
      <Link mess={POSI} open={open} />
      <style jsx>{`
        .link-ctn {
          width: 80%;
          margin: 20px auto;
          position: relative;
        }
        .nice {
          position: absolute;
          left: 0;
          top: 0;
          width: 100%;
          z-index: 10;
        }
        .main {
          padding: 10px 20px;
          background: #212329;
          border-radius: 6px;
          display: flex;
          align-items: center;
        }

        .main > .color {
          width: 66px;
          height: 40px;
          border-radius: 4px;
          margin: 0 20px;
          background: #4eac6d;
        }
        .main > .title {
          flex: 1;
          font-size: 30px;
          text-align: center;
        }
      `}</style>
    </div>
  );
};
