import React from "react";

const Nav = ({ restEvent }) => {
  const submitEvent = async (e) => {
    e.preventDefault();

    let data = new FormData(e.target);
    let typeName = document.querySelector(".component-code-typeName")
      .textContent;
    let typeSort = document.querySelector(".component-sort-name").value;
    if (!typeName && !typeSort) return;
    data.append("type", typeSort);
    try {
      let fileDom = document.querySelector('input[name="photoFile"]');
      data.append("photoFile", fileDom.files[0]);
      const QURL = process.env.DATA_URL + "addComponent/" + typeName;
      let res = await fetch(QURL, {
        method: "POST",
        body: data,
      });
      if (res.ok) {
        restEvent(1);
      } else {
        alert("添加失败");
      }
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <form className="row px-0" onSubmit={submitEvent}>
      <textarea
        name="code"
        id="code"
        className="col-10 mx-auto"
        required
      ></textarea>

      <input
        type="text"
        name="codeTitle"
        placeholder="标题"
        required
        className="col-6 mx-auto mt-5 text-center"
      />

      <div className="col-12 mt-5">
        <div className="row justify-content-center">
          <button type="submit  col-auto">Commit</button>
        </div>
      </div>

      <style jsx>
        {`
          textarea {
            height: 600px;
            font-size: 2rem;
            font-weight: 600;
            padding: 2rem;
          }
          input {
            padding: 1rem 2rem;
            font-weight: 600;
            font-size: 2rem;
          }
          button {
            border: none;
            background: #4b30c3;
            color: white;
            padding: 1rem 2rem;
            font-weight: 600;
            font-size: 2rem;
          }
        `}
      </style>
    </form>
  );
};

export default Nav;
